import React from 'react';
import SignInForm from '../components/form/signInForm';
import '../assets/styles/signIn.style.scss';
import { useEffect, useState } from "react";
import SignInObject from '../components/sliderObject/signInObject'

//onepage scroll source : https://velog.io/@svk5496/React-JS-%EC%9B%90%ED%8E%98%EC%9D%B4%EC%A7%80-%EC%8A%A4%ED%81%AC%EB%A1%A4-%EA%B5%AC%ED%98%84%ED%95%98%EA%B8%B0

const SignIn = () => {
  let num = 0;
let idx = 0;
let delta;
const mainImg = document.querySelector(".photozone");
const mainTit = document.querySelector(".main_tit01");
const elMainCon = document.querySelectorAll(".main-page");

$(window).on('mousewheel DOMMouseScroll', function (e) {
    delta = e.originalEvent.wheelDelta || e.originalEvent.detail * -1;
    if (delta < 0) {
        if (!(num == 12)) {
            num++;
            if (num <= 11) {
                mainImg.style = `width:${(num * 5) + 50}vw; height:${(num * 5) + 50}vh;`;
            }
        }
        if ((num == 12) && (idx < elMainCon.length)) {
            idx++;
        }
    } else {
        if (!(idx == 0)) {
            idx--;
        }
        if ((num > 0) && (idx == 0)) {
            num--;
            mainImg.style = `width:${(num * 5) + 50}vw; height:${(num * 5) + 50}vh;`;
        }
    }
    
    if (num >= 10) {
        mainTit.style = `color:#fff;`;
        mainImg.classList.add("on");
    } else {
        mainTit.style = `color:#000;`;
        mainImg.classList.remove("on");
    }

    $('html,body').stop().animate({
        scrollTop: ($(window).height()) * idx
    }, 600)
})
  return (
    <div className="slider">
      <SignInObject/>
      <SignInObject/>
      <SignInObject/>
      <SignInObject/>
    </div>
    
  );
};

export default SignIn;
