import SignInButton from '../button/signInButton'
import {React, useState} from 'react';
import {Link} from 'react-router-dom';
import '../../assets/styles/signInForm.style.scss';

const defaultFormFields = {
  email: '',
  password: '',
};

const SignInForm = () => {

  //Manage user's email and password by useState()
  const [formFields, setFormFields] = useState(defaultFormFields);
  const { email, password } = formFields;

  //when user enter email or password field
  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormFields({...formFields, [name]: value });
  }

  return (
    <form className='sign-in-form'>
      <label>
        이메일
        <input type="email" name="email" required onChange={handleChange} value={email}/>
      </label>
      <br />
      <label>
        비밀번호
        <input type="password" name="password" required onChange={handleChange} value={password}/>
      </label>
      <br />
      <SignInButton/>
      <Link className="sign-up-link" to='/sign-up'>
          Sign Up
      </Link>
    </form>
  );
};

export default SignInForm;
