import React from 'react';
import SignInForm from '../components/form/signInForm';
import '../assets/styles/signIn.style.scss';
import { useEffect, useState } from "react";

//onepage scroll source : https://velog.io/@svk5496/React-JS-%EC%9B%90%ED%8E%98%EC%9D%B4%EC%A7%80-%EC%8A%A4%ED%81%AC%EB%A1%A4-%EA%B5%AC%ED%98%84%ED%95%98%EA%B8%B0
function HomeWeb() {
  const wholePage = document.getElementsByClassName("slider");
  const totalPageNumber = wholePage[0]?.children?.length;

  const [currentInputs, setCurrentInputs] = useState({
    currentWindowHeight: window.innerHeight,
    currentPage: 0,
  });

  //when window resize, refactoring window size
  const setPageSize = () => {
    setCurrentInputs({ currentWindowHeight: window.innerHeight });
  };

  //fuction that return current page
  const setPage = () => {
    for (var i = 1; i < totalPageNumber; i++) {
      if (window.scrollY < currentInputs.currentWindowHeight * i) {
        setCurrentInputs({ ...currentInputs, currentPage: i });
        return;
      }
    }
  };
  
  // escape infinite loop between Scroll event and Resize
  useEffect(() => {
    window.addEventListener("scroll", setPage);
    window.addEventListener("resize", setPageSize);
    return () => {
      window.removeEventListener("scroll", setPage);
      window.removeEventListener("resize", setPageSize);
    };
  });

  window.addEventListener("wheel", (e) => {
    // when wheel down
    if (e.deltaY > 0) {
      let p = 1;
      while (p < totalPageNumber) {
        if (currentInputs.currentPage === p) {
          window.scrollTo({
            top: currentInputs.currentWindowHeight * p,
            behavior: "smooth",
          });
        }
        p++;
      }
    }
    // when wheel up
    if (e.deltaY < 0) {
      let p = 1;
      while (p < totalPageNumber) {
        if (currentInputs.currentPage === p) {
          window.scrollTo({
            top: currentInputs.currentWindowHeight * (p - 1),
            behavior: "smooth",
          });
        }
        p++;
      }
    }
  });
}

const SignIn = () => {
  return (
    <base>
        <div className="sign-in">
          <div className="sign-in-left">
            <h1 className='introduction'>우리 웹페이지를 소개하는 한 문장</h1>
          </div>
          <div className="sign-in-right">
            <SignInForm />
          </div>
        </div>
    </base>
  );
};

export default SignIn;
